mapper.update:
update album
set name = #{name,jdbcType=VARCHAR},
  head = #{head,jdbcType=VARCHAR},
  ctime = #{ctime,jdbcType=BIGINT},
  utime = #{utime,jdbcType=BIGINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, head, ctime, utime
from album
where id = #{id,jdbcType=INTEGER}
